import React from 'react';
import './GraphGrid.css';

const GraphGrid = ({ receiverPosition }) => (
  <div className="graph-container">
    {/* Y-axis labels */}
    <div className="y-axis">
      {[...Array(9)].map((_, index) => (
        <div key={index} className="axis-label">{index}</div>
      ))}
    </div>

    <div className="grid-and-x-axis">
      {/* Grid */}
      <div className="graph-grid">
        {[...Array(64)].map((_, index) => (
          <div key={index} className="grid-cell"></div>
        ))}
        {/* Receiver (red square) */}
        <div
          className="red-square"
          style={{
            top: `calc(${receiverPosition.row} * 100px)`,
            left: `calc(${receiverPosition.column} * 100px)`,
          }}
        ></div>
      </div>

      {/* X-axis labels */}
      <div className="x-axis">
        {[...Array(9)].map((_, index) => (
          <div key={index} className="axis-label">{index}</div>
        ))}
      </div>
    </div>
  </div>
);

export default GraphGrid;
